import { Component } from "react";
import emptyheart from "./emptyheart.jpg";
export class Heart extends Component {
    state = {
        CTA: "Like us!",
        image: emptyheart
    }

    render() {
        return <div>
            <div className="container">
           <h1>{this.state.CTA}</h1>
           </div>
           <div className="container">
           <img src= {this.state.image} width="120px"/>
           </div>
        </div>
    }
}
